INSERT INTO `config` VALUES (1,'缀美画室|广州缀美美术学校官网-广州画室','<p style=\"text-align:center;\">\r\n	<span style=\"font-size:18px;font-family:NSimSun;\"><strong>学校简介</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:24px;font-family:FangSong_GB2312;\"><strong><span style=\"font-family:NSimSun;font-size:18px;\">缀美美术学校成立于2006年，至今成立有10年办学历史。</span></strong><span style=\"font-family:NSimSun;font-size:18px;\">现已成为广东省知名的艺考教育培训机构。于2012年注册并正式更名为缀美美术学校。</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:18px;font-family:NSimSun;\">校区位于广州市海珠区有“广州画家村”之称的小洲村，毗邻大学城，交通方便，生活设施齐全，拥有两层四百平方教学面积，优美而宁静的师生课间休息区，专业展厅，先进的多媒体电教设备。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:18px;font-family:NSimSun;\">缀美深受广大学生的欢迎和信赖，源于它的出类拔萃，教学风格独特的教师团队，勇于创新的教学理念，别具一格的训练方法。准确无误的高考信息，科学严谨的管理方法以及精品的办学成果。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:18px;font-family:NSimSun;\">缀美是你勤奋好学加上我的辛勤付出让你迈进艺术殿堂的成功之门，走进门槛，是你找到成果的起点，是青春与智慧的证明，是你拼搏与收获的开端，在奋斗中拥有人生的辉煌。&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:18px;font-family:NSimSun;\">缀美有一班技艺双全的掌舵者，他们都是在艺术的海洋中翱翔多年的勤劳之师，用他们的智慧和经验为你的高考之舟把舵，成就你的梦想。&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\">&nbsp;&nbsp;</span><span style=\"font-size:18px;font-family:NSimSun;\">缀美有一班技艺双全的掌舵者，他们都在高考中创造辉煌的高考状元和精英，为你树立正确的航标，带领你去铸造自己辉煌和成功。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;font-family:NSimSun;\"> </span> \r\n</p>\r\n<p style=\"text-align:center;font-size:13px;font-family:&quot;text-indent:2em;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-size:18px;\"><strong><span style=\"color:#4C33E5;\">校长：</span></strong><span style=\"color:#4C33E5;\"><strong>&nbsp;庞铨进&nbsp;</strong></span></span></strong> \r\n</p>\r\n<p style=\"text-align:center;font-size:13px;font-family:&quot;text-indent:2em;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"text-align:center;font-size:13px;font-family:&quot;background-color:#FFFFFF;\">\r\n	<img width=\"300\" height=\"400\" title=\"\" align=\"\" alt=\"\" src=\"/file/image/20160804/%E5%BA%9E%E9%93%A8%E8%BF%9B%E8%BF%91%E7%85%A7.jpg\" /> \r\n</p>\r\n<p style=\"font-size:13px;font-family:&quot;text-align:center;text-indent:2em;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;</strong> \r\n</div>\r\n<span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;毕业于广州美术学院雕塑系。</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;江湖人称大庞，现居广州。</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>广州缀美艺考学校负责人，考前资深培训教师！</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;从事一线美术高考十余年，擅长素描、速写、水彩，是多家出版社供稿人。</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;教学经验丰富，幽默沉稳、踏实勤奋，善于针对性解决学生问题，深受学生的信赖和喜爱。</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp;出版与参与出版书籍有：</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</strong> \r\n</div>\r\n</span><span style=\"font-size:16px;font-family:SimSun;color:#E53333;\"> \r\n<div style=\"text-align:center;\">\r\n	<strong>《起点静物素描教学》、《诠释素描头像》等等。</strong> \r\n</div>\r\n</span> \r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>','<meta name=\"keywords\" content=\"广州画室，广州美术培训机构，广州缀美美术学校官网|缀美画室，美术培训，美术高考培训\"/>\r\n<meta name=\"description\" content=\"广州缀美美术学校官网|缀美画室作为广州画室的佼佼者为各大美术院校和企业输送了大量的美术人才，在广州美术培训机构中排名前列。\"/>\r\n','扫描下方二维码','{\"1\":{\"name\":\"jlive\",\"limit\":6},\r\n\"2\":{\"name\":\"woderact\",\"limit\":6},\r\n\"3\":{\"name\":\"funnews\",\"limit\":6},\r\n\"4\":{\"name\":\"happy\",\"limit\":6},\r\n\"5\":{\"name\":\"mnews\",\"limit\":6},\r\n\"6\":{\"name\":\"sc\",\"limit\":6}}','/file/image/20160804/20160804221639psb.jpg','缀美美术学校成立于2006年，至今成立有10年办学历史。现已成为广东省知名的艺考教育培训机构。','beautyart','','<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;\">我们正在改变，适应互联网</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:21px;\">缀美官网正式启动，更多精彩</span><span style=\"font-size:16px;line-height:1.5;\">扫描校长个人二维码或公众号</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:16px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><img width=\"300\" height=\"300\" title=\"\" align=\"\" alt=\"\" src=\"/file/image/20160805/640.jpg\" />&nbsp;&nbsp;<img width=\"300\" height=\"247\" title=\"\" align=\"\" alt=\"\" src=\"/file/image/20160804/108846154041355085.png\" /><span style=\"font-size:16px;\"></span><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:16px;\">Copyright &copy; 2012-2018, 精彩演绎 All rights reserved</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:16px;\">版权所有 &copy;广州缀美美术学校官网 &nbsp;</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;font-size:16px;\">广州画室，广州美术培训机构，广州缀美美术学校官网|缀美画室，美术培训，美术高考培训<br />\r\n</span> \r\n</p>\r\n<p style=\"text-align:center;\">\r\n	<span style=\"font-size:14px;line-height:21px;\"><span style=\"font-size:16px;\">版面参考：</span><a href=\"http://www.caa.edu.cn/index.html\" target=\"_blank\"><span style=\"font-size:16px;\">中国美术学院</span></a></span> \r\n</p>');
